import 'package:expopharma/pages/avene.dart';import 'package:expopharma/pages/categories.dart';import 'package:expopharma/pages/home.dart';import 'package:expopharma/pages/svr.dart';import 'package:expopharma/pages/vichy.dart';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import './pages/categories.dart';void main() => runApp(MyApp());class MyApp extends StatelessWidget {  @override  Widget build(BuildContext context) {    return MaterialApp(        debugShowCheckedModeBanner: false,        title: 'Exposition',        home: Home(),    routes: {          'categories': (context){    return Categories() ;    },      'vichy': (context){        return Vichy() ;      },      'svr': (context){        return Svr() ;      },      'avene': (context){       return Avene() ;          }    }    );  }}