import 'package:expopharma/compount/artlist.dart';import 'package:expopharma/pages/ajoutImage.dart';import 'package:expopharma/pages/artlist.dart';import 'package:expopharma/pages/avene.dart';import 'package:expopharma/pages/categories.dart';import 'package:expopharma/pages/detaiart.dart';import 'package:expopharma/pages/detailArticle.dart';import 'package:expopharma/pages/home.dart';import 'package:expopharma/pages/login.dart';import 'package:expopharma/pages/svr.dart';import 'package:expopharma/pages/vichy.dart';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import './pages/categories.dart';import './pages/test.dart';import 'package:firebase_core/firebase_core.dart';void main() async{  WidgetsFlutterBinding.ensureInitialized();  await Firebase.initializeApp();  runApp(MyApp());}class MyApp extends StatelessWidget {  @override  Widget build(BuildContext context) {    return MaterialApp(        debugShowCheckedModeBanner: false,        title: 'Exposition',        home: Home(),    routes: {          'categories': (context){    return Categories() ;    },      'vichy': (context){        return Vichy() ;      },      'svr': (context){        return Svr() ;      },      'avene': (context){       return Avene() ;          },      'ajoutImage': (context){        return AjoutImage() ;      },      'login': (context){        return Login() ;      },      'artlist': (context){        return ArticleList() ;      },    }    );  }}